if (!jsBaseUrl) {
jsBaseUrl = load(jsBaseUrls)[1];
java.toast(jsBaseUrl);
cache.put(`sourceVariable_${source.getKey()}/`, jsBaseUrl)
}

String.prototype.toAbsUrl=function(){
	return /^\.\/|^http|^content|^\/sdcard|^\/storage/.test(this) ? this : jsBaseUrl.concat(this)
};
java.log(jsBaseUrl);
