	return /^\.\/|^http|^content|^\/sdcard|^\/storage/.test(this) ? this : jsBaseUrl.concat(this)
}
//解决跨域js加载问题
function loadJS(urls){

	return jsBaseUrl && urls.map(url=>script(java.importScript(url.toAbsUrl())))
	.join("\n") || ""
	}

function script(content){
